import React from 'react';

const LocalizacaoMapa = ({ localizacao }) => {
  // Verificar se temos dados de localiza√ß√£o v√°lidos
  if (!localizacao || !localizacao.latitude || !localizacao.longitude) {
    return (
      <div className="bg-gray-100 p-4 rounded-lg text-center">
        <p className="text-gray-500">üìç Localiza√ß√£o n√£o dispon√≠vel</p>
      </div>
    );
  }

  const { latitude, longitude, cidade, uf } = localizacao;
  const cidadeUF = `${cidade}, ${uf}`;
  
  // URL do OpenStreetMap com marcador
  const mapUrl = `https://www.openstreetmap.org/export/embed.html?bbox=${longitude-0.01},${latitude-0.01},${longitude+0.01},${latitude+0.01}&layer=mapnik&marker=${latitude},${longitude}`;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="p-4 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-800 flex items-center">
          üìç Localiza√ß√£o da Ag√™ncia
        </h3>
        <p className="text-sm text-gray-600 mt-1">{cidadeUF}</p>
      </div>
      
      <div className="h-64 w-full">
        <iframe
          width="100%"
          height="100%"
          frameBorder="0"
          scrolling="no"
          marginHeight="0"
          marginWidth="0"
          src={mapUrl}
          title={`Mapa da localiza√ß√£o: ${cidadeUF}`}
          className="rounded-b-lg"
        />
      </div>
      
      <div className="p-3 bg-gray-50 text-xs text-gray-500 text-center">
        Coordenadas: {latitude.toFixed(4)}, {longitude.toFixed(4)}
      </div>
    </div>
  );
};

export default LocalizacaoMapa;
