import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import TableComunicacoes from '../components/TableComunicacoes';

export default function Dashboard() {
  const [resumo, setResumo] = useState(null);
  const [comunicacoes, setComunicacoes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDados() {
      setLoading(true);
      setErro('');
      try {
        // Buscar resumo e comunicações (ajustar endpoint conforme backend)
        const r1 = await fetch('http://localhost:8080/api/dashboard-resumo');
        const r2 = await fetch('http://localhost:8080/api/comunicacoes');
        const resumoData = await r1.json();
        const comunicacoesData = await r2.json();
        setResumo(resumoData);
        setComunicacoes(comunicacoesData);
      } catch (err) {
        setErro('Erro ao buscar dados do dashboard.');
      } finally {
        setLoading(false);
      }
    }
    fetchDados();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-blue-300">
      <Navbar />
      <div className="max-w-7xl mx-auto py-8 px-4">
        <h1 className="text-3xl font-bold text-blue-900 mb-6">Dashboard</h1>
        {loading && <div>Carregando...</div>}
        {erro && <div className="text-red-600 mb-4">{erro}</div>}
        {resumo && (
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
            <div className="bg-white rounded shadow p-4">
              <div className="text-gray-500">Volume Analisado</div>
              <div className="text-2xl font-bold">R$ {resumo.volume_total?.toLocaleString('pt-BR')}</div>
            </div>
            <div className="bg-white rounded shadow p-4">
              <div className="text-gray-500">Comunicações</div>
              <div className="text-2xl font-bold">{resumo.num_comunicacoes}</div>
            </div>
            <div className="bg-white rounded shadow p-4">
              <div className="text-gray-500">Principais Bancos</div>
              <div className="text-lg font-bold">{resumo.principais_bancos?.join(', ')}</div>
            </div>
            <div className="bg-white rounded shadow p-4">
              <div className="text-gray-500">Principais Envolvidos</div>
              <div className="text-lg font-bold">{resumo.principais_envolvidos?.join(', ')}</div>
            </div>
          </div>
        )}
        <div className="bg-white rounded shadow p-4">
          <h2 className="text-xl font-bold mb-4">Comunicações</h2>
          <TableComunicacoes comunicacoes={comunicacoes} />
        </div>
      </div>
    </div>
  );
}
