import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Upload() {
  const [comunicacoes, setComunicacoes] = useState(null);
  const [envolvidos, setEnvolvidos] = useState(null);
  const [ocorrencias, setOcorrencias] = useState(null);
  const [msg, setMsg] = useState('');
  const [erro, setErro] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const usuario = localStorage.getItem('usuario');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg(''); setErro(''); setLoading(true);
    const formData = new FormData();
    formData.append('comunicacoes', comunicacoes);
    formData.append('envolvidos', envolvidos);
    formData.append('ocorrencias', ocorrencias);
    formData.append('usuario', usuario);
    try {
      const resp = await fetch('http://localhost:8080/upload', {
        method: 'POST',
        body: formData,
      });
      const data = await resp.json();
      if (data.success) {
        setMsg('Arquivos enviados com sucesso!');
      } else {
        setErro(data.msg || 'Erro ao enviar arquivos.');
      }
    } catch (err) {
      setErro('Erro de conexão com o servidor.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-tl from-gray-900 via-gray-200 to-gray-100">
      <div className="w-full max-w-lg bg-white rounded-2xl shadow-lg p-8">
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-900">Upload de Arquivos</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700">Comunicacoes.csv</label>
            <input type="file" accept=".csv" required onChange={e => setComunicacoes(e.target.files[0])} className="mt-1 w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50" />
          </div>
          <div>
            <label className="block text-gray-700">Envolvidos.csv</label>
            <input type="file" accept=".csv" required onChange={e => setEnvolvidos(e.target.files[0])} className="mt-1 w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50" />
          </div>
          <div>
            <label className="block text-gray-700">Ocorrencias.csv</label>
            <input type="file" accept=".csv" required onChange={e => setOcorrencias(e.target.files[0])} className="mt-1 w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400 bg-gray-50" />
          </div>
          {msg && <div className="text-green-600 text-sm">{msg}</div>}
          {erro && <div className="text-red-600 text-sm">{erro}</div>}
          <button type="submit" disabled={loading} className="w-full bg-blue-700 text-white py-2 rounded font-semibold hover:bg-blue-800 transition disabled:opacity-50 mb-2">
            {loading ? 'Enviando...' : 'Enviar Arquivos'}
          </button>
          <button type="button" onClick={() => navigate('/dashboard')} className="w-full bg-gray-200 text-blue-900 py-2 rounded hover:bg-gray-300 transition">
            Ir para Dashboard
          </button>
        </form>
      </div>
    </div>
  );
}
