import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix para √≠cones do Leaflet no React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const LocalizacaoMapa = ({ localizacao }) => {
  // Verificar se temos dados de localiza√ß√£o v√°lidos
  if (!localizacao || !localizacao.latitude || !localizacao.longitude) {
    return (
      <div className="bg-gray-100 p-4 rounded-lg text-center">
        <p className="text-gray-500">üìç Localiza√ß√£o n√£o dispon√≠vel</p>
      </div>
    );
  }

  const position = [localizacao.latitude, localizacao.longitude];
  const cidadeUF = `${localizacao.cidade}, ${localizacao.uf}`;

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden">
      <div className="p-4 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-800 flex items-center">
          üìç Localiza√ß√£o da Ag√™ncia
        </h3>
        <p className="text-sm text-gray-600 mt-1">{cidadeUF}</p>
      </div>
      
      <div className="h-64 w-full">
        <MapContainer 
          center={position} 
          zoom={13} 
          style={{ height: '100%', width: '100%' }}
          className="rounded-b-lg"
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={position}>
            <Popup>
              <div className="text-center">
                <strong className="text-blue-600">{cidadeUF}</strong>
                <br />
                <span className="text-sm text-gray-500">
                  Ag√™ncia Banc√°ria
                </span>
              </div>
            </Popup>
          </Marker>
        </MapContainer>
      </div>
      
      <div className="p-3 bg-gray-50 text-xs text-gray-500 text-center">
        Coordenadas: {localizacao.latitude.toFixed(4)}, {localizacao.longitude.toFixed(4)}
      </div>
    </div>
  );
};

export default LocalizacaoMapa;
