import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import { InformationCircleIcon, ExclamationTriangleIcon } from '@heroicons/react/24/outline';

function TabelaPessoas({ dados, titulo }) {
  if (!dados || !Array.isArray(dados) || dados.length === 0) return null;
  return (
    <div className="mb-6">
      <h3 className="font-semibold text-blue-900 mb-2 text-lg">{titulo}</h3>
      <div className="overflow-x-auto rounded-lg border border-gray-100">
        <table className="w-full bg-gray-50 rounded text-sm">
          <thead className="bg-gray-100 sticky top-0 z-10">
            <tr>
              <th className="px-3 py-2 text-left">Nome</th>
              <th className="px-3 py-2 text-left">CPF/CNPJ</th>
              <th className="px-3 py-2 text-right">Valor</th>
              <th className="px-3 py-2 text-center">Qtde</th>
            </tr>
          </thead>
          <tbody>
            {dados.map((p, i) => (
              <tr key={i} className="border-b last:border-0">
                <td className="px-3 py-2 whitespace-nowrap">{p.nome}</td>
                <td className="px-3 py-2 whitespace-nowrap">{p.cpf_cnpj || p.cpf}</td>
                <td className="px-3 py-2 whitespace-nowrap text-right">R$ {p.valor?.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</td>
                <td className="px-3 py-2 whitespace-nowrap text-center">{p.quantidade?.toString().padStart(2, '0')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function TabelaDetalhes({ dados, titulo }) {
  if (!dados || !Array.isArray(dados) || dados.length === 0) return null;
  return (
    <div className="mb-6">
      <h3 className="font-semibold text-blue-900 mb-2 text-lg">{titulo}</h3>
      <div className="overflow-x-auto rounded-lg border border-gray-100">
        <table className="w-full bg-gray-50 rounded text-sm">
          <thead className="bg-gray-100 sticky top-0 z-10">
            <tr>
              <th className="px-3 py-2 text-left">Tipo</th>
              <th className="px-3 py-2 text-right">Valor</th>
              <th className="px-3 py-2 text-center">Quantidade</th>
              <th className="px-3 py-2 text-left">Observação</th>
            </tr>
          </thead>
          <tbody>
            {dados.map((d, i) => (
              <tr key={i} className="border-b last:border-0">
                <td className="px-3 py-2">{d.tipo}</td>
                <td className="px-3 py-2 text-right">R$ {d.valor?.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</td>
                <td className="px-3 py-2 text-center">{d.quantidade?.toString().padStart(2, '0')}</td>
                <td className="px-3 py-2">{d.observacao || d.local || (d.locais ? d.locais.join(', ') : '-')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

function TabelaBoletos({ dados }) {
  if (!dados || !Array.isArray(dados) || dados.length === 0) return null;
  return (
    <div className="mb-6">
      <h3 className="font-semibold text-blue-900 mb-2 text-lg">Pagamentos de Boletos</h3>
      <div className="overflow-x-auto rounded-lg border border-gray-100">
        <table className="w-full bg-gray-50 rounded text-sm">
          <thead className="bg-gray-100 sticky top-0 z-10">
            <tr>
              <th className="px-3 py-2 text-left">Nome Sacado</th>
              <th className="px-3 py-2 text-left">CPF/CNPJ Sacado</th>
              <th className="px-3 py-2 text-right">Valor</th>
              <th className="px-3 py-2 text-center">Qtde</th>
            </tr>
          </thead>
          <tbody>
            {dados.map((b, i) => (
              <tr key={i} className="border-b last:border-0">
                <td className="px-3 py-2 whitespace-nowrap">{b.nome_sacado}</td>
                <td className="px-3 py-2 whitespace-nowrap">{b.cpf_cnpj_sacado}</td>
                <td className="px-3 py-2 whitespace-nowrap text-right">R$ {b.valor?.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</td>
                <td className="px-3 py-2 whitespace-nowrap text-center">{b.quantidade?.toString().padStart(2, '0')}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default function DetalheComunicacao() {
  const { id } = useParams();
  const [comunicacao, setComunicacao] = useState(null);
  const [loading, setLoading] = useState(true);
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDetalhe() {
      setLoading(true);
      setErro('');
      try {
        const resp = await fetch(`http://localhost:8080/api/comunicacao/${id}`);
        if (!resp.ok) throw new Error('Não encontrado');
        const data = await resp.json();
        setComunicacao(data);
      } catch (err) {
        setErro('Erro ao buscar detalhes da comunicação.');
      } finally {
        setLoading(false);
      }
    }
    fetchDetalhe();
  }, [id]);

  const json = comunicacao?.parsing_json || {};

  return (
    <div className="min-h-screen flex flex-col items-center bg-gradient-to-tl from-gray-600 via-gray-200 to-gray-100">
      <div className="w-full max-w-4xl mx-auto pt-8 px-2 md:px-6">
        <Navbar />
        <button onClick={() => navigate(-1)} className="mb-4 text-blue-700 hover:underline">← Voltar</button>
        <h1 className="text-2xl font-bold text-blue-900 mb-4">Detalhes da Comunicação</h1>
        {loading && <div>Carregando...</div>}
        {erro && <div className="text-red-600 mb-4">{erro}</div>}
        {comunicacao && (
          <div className="bg-white rounded-2xl shadow-sm p-6 border border-gray-100 mb-6">
            {/* Seção: Dados principais */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div><span className="font-semibold text-gray-700">Titular:</span> {comunicacao.titular}</div>
              <div><span className="font-semibold text-gray-700">CPF/CNPJ:</span> {comunicacao.cpf}</div>
              <div><span className="font-semibold text-gray-700">Banco:</span> {comunicacao.banco}</div>
              <div><span className="font-semibold text-gray-700">Período:</span> {comunicacao.periodo ? `${comunicacao.periodo.inicio || ''} - ${comunicacao.periodo.fim || ''}` : '-'}</div>
              <div><span className="font-semibold text-gray-700">Valor:</span> R$ {comunicacao.valor?.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</div>
            </div>

            {/* Seção: Cônjuge */}
            {json.conjuge && (
              <div className="mb-6">
                <div className="bg-blue-50 rounded-lg p-4 border border-blue-100">
                  <span className="text-blue-700 text-sm font-medium">Cônjuge:</span>
                  <span className="text-blue-900 font-bold ml-2">{json.conjuge.nome} (CPF: {json.conjuge.cpf})</span>
                </div>
              </div>
            )}

            {/* Seção: Ocupação e Renda */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              {json.ocupacao && (
                <div className="bg-gray-50 rounded-lg p-4 flex flex-col items-start border border-gray-100">
                  <span className="text-gray-500 text-sm mb-1">Ocupação</span>
                  <span className="text-lg font-bold text-blue-900">{json.ocupacao}</span>
                </div>
              )}
              {json.renda_mensal_declarada && (
                <div className="bg-gray-50 rounded-lg p-4 flex flex-col items-start border border-gray-100">
                  <span className="text-gray-500 text-sm mb-1">Renda Mensal Declarada</span>
                  <span className="text-lg font-bold text-blue-900">R$ {json.renda_mensal_declarada.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</span>
                </div>
              )}
            </div>

            {/* Seção: Período Analisado */}
            {json.periodo && (
              <div className="mb-6">
                <div className="bg-blue-50 rounded-lg p-4 border border-blue-100">
                  <span className="text-blue-700 text-sm font-medium">Período Analisado:</span>
                  <span className="text-blue-900 font-bold ml-2">{json.periodo.inicio} até {json.periodo.fim}</span>
                </div>
              </div>
            )}

            {/* Seção: Locais de Depósitos */}
            {json.locais_depositos && json.locais_depositos.length > 0 && (
              <div className="mb-6">
                <div className="bg-green-50 rounded-lg p-4 border border-green-100">
                  <span className="text-green-700 text-sm font-medium">Locais dos Depósitos:</span>
                  <span className="text-green-900 font-bold ml-2">{json.locais_depositos.join(', ')}</span>
                </div>
              </div>
            )}

            {/* Seção: Totais de Transações */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              {json.transacoes_credito?.total && (
                <div className="bg-green-50 rounded-lg p-4 flex flex-col items-start border border-green-100">
                  <span className="text-green-600 text-sm mb-1">Total de Créditos</span>
                  <span className="text-lg font-bold text-green-800">R$ {json.transacoes_credito.total.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</span>
                </div>
              )}
              {json.transacoes_debito?.total && (
                <div className="bg-red-50 rounded-lg p-4 flex flex-col items-start border border-red-100">
                  <span className="text-red-600 text-sm mb-1">Total de Débitos</span>
                  <span className="text-lg font-bold text-red-800">R$ {json.transacoes_debito.total.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</span>
                </div>
              )}
            </div>

            {/* Seção: Detalhes das Transações de Crédito */}
            {json.transacoes_credito?.detalhes && (
              <TabelaDetalhes dados={json.transacoes_credito.detalhes} titulo="Detalhes das Transações de Crédito" />
            )}

            {/* Seção: Detalhes das Transações de Débito */}
            {json.transacoes_debito?.detalhes && (
              <TabelaDetalhes dados={json.transacoes_debito.detalhes} titulo="Detalhes das Transações de Débito" />
            )}

            {/* Seção: Tabelas de pessoas */}
            <TabelaPessoas dados={json.transacoes_credito?.principais_depositantes} titulo="Principais Depositantes/Remetentes" />
            <TabelaPessoas dados={json.transacoes_debito?.principais_favorecidos} titulo="Principais Favorecidos/Beneficiários" />

            {/* Seção: Outras Transações */}
            {json.outras_transacoes && json.outras_transacoes.length > 0 && (
              <div className="mb-6">
                <h3 className="font-semibold text-blue-900 mb-2 text-lg">Outras Transações</h3>
                <div className="bg-gray-50 rounded-lg p-4 border border-gray-100">
                  {json.outras_transacoes.map((t, i) => (
                    <div key={i} className="mb-2 last:mb-0">
                      <span className="font-medium">{t.tipo}:</span>
                      {t.valor_aplicado && <span className="ml-2">Aplicado: R$ {t.valor_aplicado.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</span>}
                      {t.valor_resgatado && <span className="ml-2">Resgatado: R$ {t.valor_resgatado.toLocaleString('pt-BR', {minimumFractionDigits: 2, maximumFractionDigits: 2})}</span>}
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Seção: Observações */}
            {json.observacoes && json.observacoes.length > 0 && (
              <div className="mb-6">
                <h3 className="font-semibold text-blue-900 mb-2 flex items-center gap-2"><InformationCircleIcon className="w-5 h-5 text-blue-400" /> Observações</h3>
                <ul className="list-disc ml-6 text-gray-700">
                  {json.observacoes.map((o, i) => <li key={i}>{o}</li>)}
                </ul>
              </div>
            )}

            {/* Seção: Análise de Fraude */}
            {json.analise_fraude && json.analise_fraude.length > 0 && (
              <div className="mb-6">
                <h3 className="font-semibold text-red-700 mb-2 flex items-center gap-2"><ExclamationTriangleIcon className="w-5 h-5 text-red-400" /> Análise de Fraude</h3>
                <ul className="list-disc ml-6 text-red-700">
                  {json.analise_fraude.map((a, i) => <li key={i}>{a}</li>)}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
