import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

function TabelaPessoas({ dados, titulo }) {
  if (!dados || !Array.isArray(dados) || dados.length === 0) return null;
  return (
    <div className="mb-4">
      <h3 className="font-semibold text-blue-900 mb-2">{titulo}</h3>
      <div className="overflow-x-auto">
        <table className="w-full bg-gray-50 rounded text-sm">
          <thead>
            <tr>
              <th className="px-2 py-1 text-left">Nome</th>
              <th className="px-2 py-1 text-left">CPF/CNPJ</th>
              <th className="px-2 py-1 text-left">Banco</th>
              <th className="px-2 py-1 text-right">Valor</th>
              <th className="px-2 py-1 text-center">Qtde</th>
            </tr>
          </thead>
          <tbody>
            {dados.map((p, i) => (
              <tr key={i} className="border-b last:border-0">
                <td className="px-2 py-1 whitespace-nowrap">{p.nome}</td>
                <td className="px-2 py-1 whitespace-nowrap">{p.cpf_cnpj}</td>
                <td className="px-2 py-1 whitespace-nowrap">{p.banco}</td>
                <td className="px-2 py-1 whitespace-nowrap text-right">R$ {p.valor?.toLocaleString('pt-BR')}</td>
                <td className="px-2 py-1 whitespace-nowrap text-center">{p.quantidade}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default function DetalheComunicacao() {
  const { id } = useParams();
  const [comunicacao, setComunicacao] = useState(null);
  const [loading, setLoading] = useState(true);
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDetalhe() {
      setLoading(true);
      setErro('');
      try {
        const resp = await fetch(`http://localhost:8080/api/comunicacao/${id}`);
        if (!resp.ok) throw new Error('Não encontrado');
        const data = await resp.json();
        setComunicacao(data);
      } catch (err) {
        setErro('Erro ao buscar detalhes da comunicação.');
      } finally {
        setLoading(false);
      }
    }
    fetchDetalhe();
  }, [id]);

  const json = comunicacao?.parsing_json || {};

  return (
    <div className="min-h-screen flex flex-col items-center bg-gradient-to-tl from-gray-600 via-gray-200 to-gray-100">
      <div className="w-full max-w-4xl mx-auto pt-8 px-2 md:px-6">
        <Navbar />
        <button onClick={() => navigate(-1)} className="mb-4 text-blue-700 hover:underline">← Voltar</button>
        <h1 className="text-2xl font-bold text-blue-900 mb-4">Detalhes da Comunicação</h1>
        {loading && <div>Carregando...</div>}
        {erro && <div className="text-red-600 mb-4">{erro}</div>}
        {comunicacao && (
          <div className="bg-white rounded-2xl shadow-sm p-6 border border-gray-100 mb-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div><span className="font-semibold text-gray-700">Titular:</span> {comunicacao.titular}</div>
              <div><span className="font-semibold text-gray-700">CPF/CNPJ:</span> {comunicacao.cpf}</div>
              <div><span className="font-semibold text-gray-700">Banco:</span> {comunicacao.banco}</div>
              <div><span className="font-semibold text-gray-700">Período:</span> {comunicacao.periodo ? `${comunicacao.periodo.inicio || ''} - ${comunicacao.periodo.fim || ''}` : '-'}</div>
              <div><span className="font-semibold text-gray-700">Valor:</span> R$ {comunicacao.valor?.toLocaleString('pt-BR')}</div>
            </div>
            {json.renda_mensal_declarada && (
              <div className="mb-2"><span className="font-semibold text-gray-700">Renda Mensal Declarada:</span> R$ {json.renda_mensal_declarada.toLocaleString('pt-BR')}</div>
            )}
            {json.transacoes_credito_total && (
              <div className="mb-2"><span className="font-semibold text-gray-700">Total de Créditos:</span> R$ {json.transacoes_credito_total.toLocaleString('pt-BR')}</div>
            )}
            {json.transacoes_debito_total && (
              <div className="mb-2"><span className="font-semibold text-gray-700">Total de Débitos:</span> R$ {json.transacoes_debito_total.toLocaleString('pt-BR')}</div>
            )}
            <TabelaPessoas dados={json.principais_depositantes} titulo="Principais Depositantes/Remetentes" />
            <TabelaPessoas dados={json.principais_favorecidos} titulo="Principais Favorecidos" />
            {json.observacoes && json.observacoes.length > 0 && (
              <div className="mb-4">
                <h3 className="font-semibold text-blue-900 mb-1">Observações</h3>
                <ul className="list-disc ml-6 text-gray-700">
                  {json.observacoes.map((o, i) => <li key={i}>{o}</li>)}
                </ul>
              </div>
            )}
            {json.analise_fraude && json.analise_fraude.length > 0 && (
              <div className="mb-4">
                <h3 className="font-semibold text-red-700 mb-1">Análise de Fraude</h3>
                <ul className="list-disc ml-6 text-red-700">
                  {json.analise_fraude.map((a, i) => <li key={i}>{a}</li>)}
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
