import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';

export default function DetalheComunicacao() {
  const { id } = useParams();
  const [comunicacao, setComunicacao] = useState(null);
  const [loading, setLoading] = useState(true);
  const [erro, setErro] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchDetalhe() {
      setLoading(true);
      setErro('');
      try {
        const resp = await fetch(`http://localhost:8080/api/comunicacao/${id}`);
        if (!resp.ok) throw new Error('Não encontrado');
        const data = await resp.json();
        setComunicacao(data);
      } catch (err) {
        setErro('Erro ao buscar detalhes da comunicação.');
      } finally {
        setLoading(false);
      }
    }
    fetchDetalhe();
  }, [id]);

  const json = comunicacao?.parsing_json || {};

  return (
    <div className="min-h-screen flex flex-col items-center bg-gradient-to-tl from-gray-600 via-gray-200 to-gray-100">
      <div className="w-full max-w-4xl mx-auto pt-8 px-2 md:px-6">
        <Navbar />
        <button onClick={() => navigate(-1)} className="mb-4 text-blue-700 hover:underline">← Voltar</button>
        <h1 className="text-2xl font-bold text-blue-900 mb-4">Detalhes da Comunicação</h1>
        {loading && <div>Carregando...</div>}
        {erro && <div className="text-red-600 mb-4">{erro}</div>}
        {comunicacao && (
          <div className="bg-white rounded-xl shadow p-6 border border-gray-100">
            <div className="mb-4">
              <strong>Titular:</strong> {comunicacao.titular}<br/>
              <strong>CPF:</strong> {comunicacao.cpf}<br/>
              <strong>Banco:</strong> {comunicacao.banco}<br/>
              <strong>Período:</strong> {comunicacao.periodo ? `${comunicacao.periodo.inicio || ''} - ${comunicacao.periodo.fim || ''}` : '-'}<br/>
              <strong>Valor:</strong> R$ {comunicacao.valor?.toLocaleString('pt-BR')}
            </div>
            <div>
              <h2 className="font-bold text-lg mb-2">JSON Estruturado</h2>
              <pre className="bg-gray-100 rounded p-4 text-xs overflow-x-auto max-h-96">{JSON.stringify(json, null, 2)}</pre>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
