import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [usuario, setUsuario] = useState('');
  const [senha, setSenha] = useState('');
  const [erro, setErro] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErro('');
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('usuario', usuario);
      formData.append('senha', senha);
      const resp = await fetch('http://localhost:8080/login', {
        method: 'POST',
        body: formData,
      });
      const data = await resp.json();
      if (data.success) {
        localStorage.setItem('usuario', usuario);
        navigate('/upload');
      } else {
        setErro(data.erro || 'Usuário ou senha inválidos.');
      }
    } catch (err) {
      setErro('Erro de conexão com o servidor.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-blue-300">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded shadow-md w-full max-w-sm">
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-900">RIF Analisador - Login</h2>
        <div className="mb-4">
          <label className="block text-gray-700">Usuário</label>
          <input type="text" value={usuario} onChange={e => setUsuario(e.target.value)} required className="mt-1 w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Senha</label>
          <input type="password" value={senha} onChange={e => setSenha(e.target.value)} required className="mt-1 w-full px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400" />
        </div>
        {erro && <div className="mb-4 text-red-600 text-sm">{erro}</div>}
        <button type="submit" disabled={loading} className="w-full bg-blue-700 text-white py-2 rounded hover:bg-blue-800 transition disabled:opacity-50">
          {loading ? 'Entrando...' : 'Entrar'}
        </button>
      </form>
    </div>
  );
}
